import React,{useState,useMemo} from "react";
import { Button,IdentityBadge,EthIdenticon,Header,Tabs,IconLink } from '@aragon/ui';
import useWeb3Modal from '../hooks/useWeb3Modal';
import { Link,Redirect,useLocation } from 'react-router-dom';

function Menu(){
  const {loadWeb3Modal,coinbase,netId,profile} = useWeb3Modal();
  const [selected, setSelected] = useState();

  const location = useLocation();


  useMemo(() => {
    if(!selected && location){
      if(location.pathname === "/home"){
        setSelected(0);
      }
      if(location.pathname === "/mint"){
        setSelected(1);
      }
      if(location.pathname === "/all-avatars"){
        setSelected(2);
      }
      if(location.pathname === "/profile"){
        setSelected(3);
      }

    }
  },[selected,location])

  return(
    <>
    <Header
      primary={
        <>
          <h2>SnowflakesHash</h2>
          {
            netId &&
            (
              netId === 4 ?
              <>
              <img alt="" src="data:image/png;base64," style={{width: '25px'}}/>
              <small>RINKEBY</small>
              </> :
              netId === 0x64 ?
              <>
              <img alt="" src={require("../assets/xdai-logo.png")} style={{width: '25px'}}/>
              <small>XDAI</small>
              </> :
              "WRONG NETWORK"
            )
          }
        </>
      }
      secondary={
        <>
        {
          coinbase ?
          <IdentityBadge
            customLabel={profile?.name}
            entity={coinbase}
            connectedAccount
            popoverTitle={profile?.name}
            icon={profile?.image ?
                  <img alt="" src={profile.image.original.src.replace("ipfs://","https://ipfs.io/ipfs/")} style={{width: '25px'}} /> :
                  <EthIdenticon address={coinbase}/>
            }
          /> :
          <Button
            onClick={() => {
              if (!coinbase && window.ethereum) {
                loadWeb3Modal();
              } else if(!window.ethereum){
                window.open("https://metamask.io/", '_blank');
              }
            }}
          >
            {!coinbase && window.ethereum ? "Connect Wallet" : !window.ethereum && <>Install Metamask <IconLink/></> }
          </Button>
        }
        </>
      }
    />
    <Tabs
      items={
        [
          <Link to="/home" style={{textDecoration: "none"}}>Informations</Link>,
          <Link to="/mint" style={{textDecoration: "none"}}><b>Generate Snowflake</b></Link>,
          <Link to="/all-avatars" style={{textDecoration: "none"}}>All Snowflakes</Link>,
          //<Link to="/games" style={{textDecoration: "none"}}>Games</Link>,
          //<Link to="/feedbacks" style={{textDecoration: "none"}}>Feedbacks</Link>,
          coinbase && <Link to="/profile" style={{textDecoration: "none"}}>Profile</Link>
        ]
      }
      selected={selected}
      onChange={setSelected}
    />
    {
      selected === 0 &&
      <Redirect to={"/home"} />
    }
    {
      selected === 1 &&
      <Redirect to={"/mint"} />
    }
    {
      selected === 2 &&
      <Redirect to={"/all-avatars"} />
    }
    {
      selected === 3 && coinbase &&
      <Redirect to={"/profile"} />
    }
    {
      selected === 4 && coinbase &&
      <Redirect to={"/profile"} />
    }

    </>
  )
}


export default Menu;
